		textPane.setText("\u4E00\u3001\u7CFB\u7EDF\u767B\u9646\r\n      \u767B\u5F55\u4E3A\u7528\u6237\u4F7F\u7528\u7CFB\u7EDF\u7684\u8D77\u59CB\u70B9\uFF0C\u7528\u6237\u5FC5\u987B\u5148\u901A\u8FC7\u8EAB\u4EFD\u9A8C\u8BC1\uFF0C\u7136\u540E\u7CFB\u7EDF\u6839\u636E\u5176\u8EAB\u4EFD\u786E\u5B9A\u4ED6\u7684\u64CD\u4F5C\u6743\u9650\u540E\uFF0C\u65B9\u53EF\u5BF9\u7535\u52A8\u8F66\u751F\u4EA7\u7CFB\u7EDF\u8FDB\u884C\u76F8\u5173\u64CD\u4F5C\u3002\r\n\r\n\r\n\u4E8C\u3001\u57FA\u672C\u8D44\u6599\u7EF4\u62A4\r\n      \u5458\u5DE5\u5728\u4F01\u4E1A\u4E2D\u8D1F\u8D23\u751F\u4EA7\u6D41\u7A0B\u4E2D\u7684\u5404\u4E2A\u4E0D\u540C\u7684\u90E8\u5206\uFF0C\u56E0\u6B64\u5177\u6709\u4E0D\u540C\u7684\u8EAB\u4EFD\u548C\u6743\u9650\u3002\u8BE5\u6A21\u5757\u4E3B\u8981\u662F\u7CFB\u7EDF\u7BA1\u7406\u5458\u5BF9\u5404\u90E8\u95E8\u7684\u90E8\u95E8\u7BA1\u7406\u5458\u548C\u4F01\u4E1A\u7BA1\u7406\u8005\u7684\u7EDF\u4E00\u7BA1\u7406\u3002\u4F01\u4E1A\u589E\u52A0\u65B0\u7684\u5458\u5DE5\u65F6\uFF0C\u7CFB\u7EDF\u7BA1\u7406\u5458\u53EF\u4EE5\u5728\u7CFB\u7EDF\u4E2D\u6DFB\u52A0\u8BE5\u5458\u5DE5\u7684\u57FA\u672C\u4FE1\u606F\uFF0C\u5982\u5458\u5DE5\u8D44\u6599\u53D1\u751F\u53D8\u52A8\u65F6\uFF0C\u53EF\u4EE5\u5BF9\u5176\u8D44\u6599\u8FDB\u884C\u4FEE\u6539\uFF0C\u5BF9\u4E8E\u79BB\u804C\u7684\u5458\u5DE5\u53EF\u4EE5\u5C06\u5176\u6807\u8BB0\u4E3A\u79BB\u804C\u3002\u53EF\u4FEE\u6539\u7684\u8D44\u6599\u5305\u62EC\u5458\u5DE5\u7F16\u53F7\u3001\u59D3\u540D\u3001\u6027\u522B\u3001\u8054\u7CFB\u65B9\u5F0F\u4E0E\u5728\u804C\u72B6\u6001\u3002\r\n\r\n\r\n\u4E09\u3001\u9500\u552E\r\n      \u5728\u9500\u552E\u7EF4\u62A4\u4E2D\u53EF\u4EE5\u5BF9\u9500\u552E\u8BA2\u5355\u548C\u9500\u552E\u5355\u8FDB\u884C\u8BBE\u7F6E\u3002\r\n\r\n\u8BA2\u5355\u7EF4\u62A4\uFF1A\r\n      \u7528\u4E8E\u5F55\u5165\u516C\u53F8\u63A5\u5230\u7684\u8BA2\u5355\u3002\u8BE5\u6A21\u5757\u8BF4\u660E\u4E86\u9500\u552E\u90E8\u95E8\u7BA1\u7406\u5458\u53EF\u4EE5\u4FEE\u6539\u8BA2\u5355\u6570\u636E\u3001\u589E\u52A0\u8BA2\u5355\u6570\u636E\u3001\u67E5\u8BE2\u8BA2\u5355\u6570\u636E\u3002\r\n\r\n\u9500\u552E\u5355\u7EF4\u62A4\uFF1A\r\n     \u7528\u4E8E\u5F55\u5165\u516C\u53F8\u7684\u9500\u552E\u5355\uFF0C\u5E76\u5C06\u9500\u552E\u5546\u54C1\u51FA\u5E93\u3002\u8BE5\u6A21\u5757\u8BF4\u660E\u4E86\u9500\u552E\u90E8\u95E8\u7BA1\u7406\u5458\u53EF\u4EE5\u4FEE\u6539\u9500\u552E\u51FA\u5E93\u5355\u636E\u3001\u589E\u52A0\u9500\u552E\u51FA\u5E93\u5355\u636E\u3001\u67E5\u8BE2\u9500\u552E\u51FA\u5E93\u5355\u636E\u3002\r\n     \u5F53\u9500\u552E\u51FA\u5E93\u5355\u53D1\u751F\u53D8\u5316\u65F6\u53EF\u4EE5\u5BF9\u5176\u8FDB\u884C\u4FEE\u6539\uFF0C\u4F46\u9700\u8981\u6EE1\u8DB3\u5982\u4E0B\u6761\u4EF6\uFF1A\u589E\u52A0\u9500\u552E\u5546\u54C1\u662F\u4FDD\u8BC1\u5F53\u524D\u5E93\u5B58\u6570-\u8BE5\u5546\u54C1\u5728\u5355\u636E\u4E0A\u7684\u6570\u91CF>=0\r\n      \u5F53\u9500\u552E\u51FA\u5E93\u65F6\uFF0C\u53EF\u4EE5\u65B0\u589E\u9500\u552E\u51FA\u5E93\u5355\u3001\u5F55\u5165\u5355\u636E\u53CA\u5355\u636E\u7684\u8BE6\u7EC6\u5185\u5BB9\u3002\u65B0\u589E\u9500\u552E\u5546\u54C1\u65F6\uFF0C\u66F4\u65B0\u6BCF\u79CD\u5546\u54C1\u7684\u5E93\u5B58\uFF0C\u66F4\u65B0\u516C\u5F0F\u4E3A\u5F53\u524D\u5E93\u5B58\u6570\u91CF=\u5F53\u524D\u5E93\u5B58\u6570\u91CF-\u672C\u6B21\u5355\u636E\u4E2D\u5546\u54C1\u6570\u91CF\u3002\r\n\r\n\r\n\u56DB\u3001\u751F\u4EA7\r\n     \u5728\u751F\u4EA7\u7EF4\u62A4\u4E2D\u53EF\u4EE5\u5BF9\u4EA7\u54C1\u751F\u4EA7\u5355\u548C\u914D\u4EF6\u751F\u4EA7\u5355\u8FDB\u884C\u8BBE\u7F6E\u3002\r\n\r\n\u6210\u54C1\u751F\u4EA7\u7EF4\u62A4\uFF1A\r\n      \u7528\u4E8E\u67E5\u8BE2\u548C\u4FEE\u6539\u516C\u53F8\u6210\u54C1\u751F\u4EA7\u8F66\u95F4\u7684\u4EA7\u54C1\u751F\u4EA7\u5355\u3002\u8BE5\u7528\u4F8B\u8BF4\u660E\u4E86\u6210\u54C1\u914D\u4EF6\u7BA1\u7406\u5458\u53EF\u4EE5\u4FEE\u6539\u6210\u54C1\u751F\u4EA7\u5355\u6570\u636E\u3001\u67E5\u8BE2\u6210\u54C1\u751F\u4EA7\u5355\u6570\u636E\u3001\u67E5\u8BE2\u751F\u4EA7\u8BA1\u5212\u3002\r\n      \u7528\u4E8E\u67E5\u8BE2\u6210\u54C1\u751F\u4EA7\u5355\u548C\u751F\u4EA7\u8BA1\u5212\uFF0C\u751F\u4EA7\u5B8C\u6210\u540E\u6309\u7167\u9500\u552E\u5355\u51FA\u5E93\u3002\u6839\u636E\u751F\u4EA7\u8BA1\u5212\uFF0C\u6210\u54C1\u8F66\u95F4\u5230\u914D\u4EF6\u5E93\u9886\u6599\uFF0C\u8981\u6C42\u6240\u9886\u7684\u914D\u4EF6\u6570\u76EE\u5E94\u7565\u5927\u4E8E\u6240\u9700\u914D\u4EF6\u6570\u76EE\u3002\u751F\u4EA7\u5B8C\u6210\u540E\uFF0C\u5E94\u5C06\u5269\u4F59\u7684\u914D\u4EF6\u6570\u91CF\u8FD4\u56DE\u914D\u4EF6\u5E93\uFF0C\u5728\u5165\u5E93\u8868\u4E2D\u6807\u8BB0\u5269\u4F59\u914D\u4EF6\u4E2D\u7684\u6210\u54C1\uFF0C\u5E76\u4E14\u5728\u5165\u5E93\u5355\u4E2D\u6807\u8BB0\u6210\u54C1\u5165\u5E93\u7684\u6765\u6E90\u4E3A\u6210\u54C1\u8F66\u95F4\u9000\u56DE\u3002\r\n      \u7528\u4E8E\u4FEE\u6539\u6210\u54C1\u751F\u4EA7\u5355\u6570\u636E\u3002\u751F\u4EA7\u8BA1\u5212\u79D1\u8BA1\u5212\u6709\u53D8\uFF0C\u6240\u4EE5\u9700\u8981\u6839\u636E\u4E0D\u540C\u72B6\u51B5\u4FEE\u6539\u9648\u54C1\u751F\u4EA7\u5355\u7684\u76F8\u5E94\u4FE1\u606F\u3002\r\n\r\n\u914D\u4EF6\u751F\u4EA7\u5355\u7EF4\u62A4\uFF1A \t\r\n      \u7528\u4E8E\u67E5\u8BE2\u548C\u4FEE\u6539\u516C\u53F8\u914D\u4EF6\u751F\u4EA7\u8F66\u95F4\u7684\u914D\u4EF6\u751F\u4EA7\u5355\u3002\u8BE5\u7528\u4F8B\u8BF4\u660E\u4E86\u914D\u4EF6\u8F66\u95F4\u7BA1\u7406\u5458\u53EF\u4EE5\u4FEE\u6539\u914D\u4EF6\u751F\u4EA7\u5355\u6570\u636E\u3001\u67E5\u8BE2\u914D\u4EF6\u751F\u4EA7\u5355\u6570\u636E\u3002\r\n      \u7528\u4E8E\u67E5\u8BE2\u914D\u4EF6\u751F\u4EA7\u5355\u6570\u636E\u3002\u914D\u4EF6\u8F66\u95F4\u6839\u636E\u914D\u4EF6\u751F\u4EA7\u5355\u751F\u4EA7\u914D\u4EF6\u3002\u8F66\u95F4\u751F\u4EA7\u8FC7\u7A0B\u4E2D\u4F1A\u6709\u90E8\u5206\u914D\u4EF6\u4E0D\u5408\u683C\uFF0C\u9700\u8981\u5728\u914D\u4EF6\u5165\u5E93\u65F6\u6807\u8BB0\u51FA\u6765\uFF0C\u5728\u914D\u4EF6\u5165\u65F6\u8FDB\u884C\u9500\u6BC1\u3002\r\n      \u7528\u4E8E\u4FEE\u6539\u914D\u4EF6\u751F\u4EA7\u5355\u6570\u636E\u3002\u914D\u4EF6\u751F\u4EA7\u8FC7\u7A0B\u4E2D\u7531\u4E8E\u8BA1\u5212\u6709\u53D8\uFF0C\u4FEE\u6539\u6240\u9700\u4FEE\u6539\u7684\u76F8\u5E94\u6570\u636E\u3002\r\n\r\n\r\n\u4E94\u3001\u751F\u4EA7\u8BA1\u5212\u7BA1\u7406\r\n      \u5728\u751F\u4EA7\u8BA1\u5212\u7BA1\u7406\u4E2D\u53EF\u4EE5\u6839\u636E\u6210\u54C1\u8BA2\u5355\u548C\u6210\u54C1\u5E93\u5B58\u5F00\u51FA\u76F8\u5E94\u7684\u6210\u54C1\u751F\u4EA7\u5355\uFF0C\u5E76\u6839\u636E\u76F8\u5E94\u8F66\u578B\u8868\u5F97\u5230\u751F\u4EA7\u8BA1\u5212\u3002\u800C\u5728\u540E\u671F\u56E0\u751F\u4EA7\u6210\u54C1\u6240\u9700\u8981\u7684\u914D\u4EF6\u4E5F\u7531\u751F\u4EA7\u8BA1\u5212\u7BA1\u7406\u6839\u636E\u914D\u4EF6\u5E93\u5B58\u548C\u751F\u4EA7\u8BA1\u5212\u5F00\u51FA\u914D\u4EF6\u751F\u4EA7\u5355\u3002\r\n\r\n\u6210\u54C1\u751F\u4EA7\u5355\u548C\u751F\u4EA7\u8BA1\u5212\u7EF4\u62A4\uFF1A\r\n       \u7528\u4E8E\u5F55\u5165\u516C\u53F8\u751F\u4EA7\u8BA1\u5212\u79D1\u7684\u6210\u54C1\u4EA7\u5355\u548C\u751F\u6210\u76F8\u5E94\u7684\u751F\u4EA7\u8BA1\u5212\u3002\u8FD9\u4E24\u4E2A\u7528\u4F8B\u8BF4\u660E\u4E86\u751F\u4EA7\u8BA1\u5212\u7BA1\u7406\u5458\u53EF\u4EE5\u5F00\u51FA\u6210\u54C1\u751F\u4EA7\u5355\u3001\u5236\u5B9A\u914D\u4EF6\u751F\u4EA7\u8BA1\u5212\uFF0C\u5E76\u67E5\u8BE2\u548C\u4FEE\u6539\u5185\u5BB9\u3002\r\n       \u7528\u4E8E\u5F00\u51FA\u6210\u54C1\u751F\u4EA7\u5355\u3001\u5236\u5B9A\u914D\u4EF6\u751F\u4EA7\u8BA1\u5212\u3002\u751F\u4EA7\u8BA1\u5212\u79D1\u6839\u636E\u8BA2\u5355\uFF0C\u5C06\u6210\u54C1\u751F\u4EA7\u5355\u53D1\u5230\u6210\u54C1\u8F66\u95F4\uFF0C\u6210\u54C1\u8F66\u95F4\u4F9D\u636E\u751F\u4EA7\u5355\u751F\u4EA7\uFF0C\u5E76\u4F9D\u636E\u76F8\u5E94\u7684\u751F\u4EA7\u8BA1\u5212\u5230\u914D\u4EF6\u5E93\u9886\u6599\u3002\r\n\r\n\u914D\u4EF6\u751F\u4EA7\u5355\u7EF4\u62A4\uFF1A\r\n       \u7528\u4E8E\u5F55\u5165\u516C\u53F8\u914D\u4EF6\u751F\u4EA7\u8F66\u95F4\u7684\u914D\u4EF6\u751F\u4EA7\u5355\u3002\u8BE5\u7528\u4F8B\u8BF4\u660E\u4E86\u914D\u4EF6\u8F66\u95F4\u7BA1\u7406\u5458\u53EF\u4EE5\u4FEE\u6539\u914D\u4EF6\u751F\u4EA7\u5355\u6570\u636E\u3001\u589E\u52A0\u914D\u4EF6\u751F\u4EA7\u5355\u636E\u3001\u67E5\u8BE2\u6210\u54C1\u751F\u4EA7\u5355\u6570\u636E\u3002\r\n       \u914D\u4EF6\u8F66\u95F4\u751F\u4EA7\u8FC7\u7A0B\u4E2D\u4F1A\u6709\u90E8\u5206\u914D\u4EF6\u4E0D\u5408\u683C\uFF0C\u9700\u8981\u5728\u914D\u4EF6\u5165\u5E93\u65F6\u6807\u8BB0\u51FA\u6765\uFF0C\u5728\u914D\u4EF6\u5165\u65F6\u8FDB\u884C\u9500\u6BC1\u3002\r\n\r\n\r\n\u516D\u3001\u5E93\u5B58\u7BA1\u7406\r\n      \u5728\u5E93\u5B58\u7BA1\u7406\u4E2D\u53EF\u4EE5\u4F9D\u636E\u5404\u7C7B\u6761\u4EF6\u5BF9\u6570\u636E\u5E93\u8FDB\u884C\u67E5\u8BE2\u548C\u914D\u4EF6\u51FA\u5165\u5E93\u7BA1\u7406\u3002\r\n\r\n\u6210\u54C1/\u914D\u4EF6\u5E93\u5B58\u67E5\u8BE2\uFF1A\r\n      \u5E93\u5B58\u662F\u81EA\u884C\u8F66\u751F\u4EA7\u7CFB\u7EDF\u7684\u4E00\u4E2A\u6838\u5FC3\u5355\u5143\uFF0C\u6BCF\u65E5\u76D8\u70B9\u5E93\u5B58\u4E5F\u662F\u4F01\u4E1A\u5FC5\u987B\u7684\u5DE5\u4F5C\uFF0C\u5E93\u5B58\u67E5\u8BE2\u53EF\u4EE5\u6839\u636E\u4E0D\u540C\u7684\u6761\u4EF6\u5BF9\u914D\u4EF6/\u6210\u54C1\u7684\u5E93\u5B58\u60C5\u51B5\u8FDB\u884C\u76D8\u70B9\u6838\u5BF9\u3002\u5E76\u53CA\u65F6\u53D1\u73B0\u548C\u5904\u7406\u5E93\u5B58\u95EE\u9898\uFF0C\u4F8B\u5982\u914D\u4EF6\u5E93\u4E2D\u7684\u6B21\u54C1\u6BD4\u91CD\u8D85\u8FC7\u4E00\u5B9A\u6BD4\u4F8B\u9700\u8981\u53CA\u65F6\u9500\u6BC1\u7B49\u3002\r\n\r\n\u914D\u4EF6\u51FA/\u5165\u5E93\u7BA1\u7406\uFF1A\r\n      \u914D\u4EF6\u51FA/\u5165\u5E93\u7BA1\u7406\u662F\u8BB0\u5F55\u914D\u4EF6\u51FA\u5E93\u5165\u5E93\u65F6\u7684\u76F8\u5173\u67E5\u8BE2\u8BB0\u5F55\u3002\u5728\u914D\u4EF6\u5165\u5E93\u65F6\uFF0C\u5165\u5E93\u7684\u65B9\u5F0F\u6709\u4E24\u79CD\uFF1A\u4E00\u79CD\u662F\u6765\u81EA\u4E8E\u914D\u4EF6\u8F66\u95F4\u7684\u751F\u4EA7\uFF0C\u53E6\u4E00\u79CD\u662F\u6765\u6E90\u4E8E\u6210\u54C1\u8F66\u95F4\u7684\u4F59\u6599\u8FD4\u56DE\u3002\u5982\u679C\u5C5E\u4E8E\u7B2C\u4E00\u79CD\u6765\u6E90\uFF0C\u5219\u5728\u5165\u5E93\u7684\u8FC7\u7A0B\u4E2D\u9700\u8981\u5C06\u914D\u4EF6\u4E2D\u7684\u6B21\u54C1\u76F4\u63A5\u9500\u6BC1\u3002\u5982\u679C\u5C5E\u4E8E\u7B2C\u4E8C\u79CD\u6765\u6E90\uFF0C\u5219\u9700\u8981\u5C06\u76F8\u5E94\u7684\u914D\u4EF6\u6B21\u54C1\u6807\u8BB0\u5E76\u91CD\u65B0\u8FD4\u56DE\u5230\u914D\u4EF6\u5E93\u4E2D\u3002\r\n   \r\n\r\n\r\n");
